// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

import { IERC20 } from "./Lib/IERC20.sol";

contract AssetGetter {

    function getAssetData(address asset, address account, address prom) external view returns (string memory, uint, uint, uint) {
      IERC20 token = IERC20(asset);
        return (token.name(), token.decimals(), token.allowance(account, prom), token.balanceOf(account));
    }

}
