// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

import { IERC20 } from "./Lib/IERC20.sol";


contract PromController {

    mapping(uint => PromData) promises;
    mapping(address => uint[]) accountPromises;
    mapping(bytes32 => uint[]) joinablePromises;
    mapping(bytes32 => uint[]) joinablePromisesRatios;

    uint public lastId;

    struct JoinableProm {
      uint id;
      uint amountA;
      uint amountB;
      uint time;
    }

    struct PromData {    
    /** ---------------------
         a is the creator
         b is the joiner
         time is the minimum date it can be executed
        --------------------- **/
      address addrA;
      uint amountA;
      address assetA;
      uint owedA;

      address addrB;
      uint amountB;
      address assetB;
      uint owedB;

      uint time;
      bool executed;
    }
    event PromiseCreated(address addrA, uint amountA, address assetA, uint amountB, address assetB, uint time);
    event PromiseJoined(address addrB, uint id);
    event PromiseExecuted(address executor, uint id);

    function createPromise(uint amountA, address assetA, uint amountB, address assetB, uint time) external {
      /* --------- REMOVE NOTATION BEFORE PRODUCTION ------------ */
      /*       require(time > block.timestamp + 1 days, "1 day is the minimum, as a fair timeframe is needed for both parties to pay in full"); */
      IERC20 token = IERC20(assetA);

      lastId += 1;
      uint id = lastId;

      promises[id] = PromData(msg.sender, amountA, assetA, amountA / 2, address(0x0), amountB, assetB, amountB, time, false);
      token.transferFrom(msg.sender, address(this), amountA / 2);

      bytes32 pairID = getPairID(assetA,assetB);
      joinablePromises[pairID].push(id);

      accountPromises[msg.sender].push(id);

      emit PromiseCreated(msg.sender, amountA, assetA, amountB, assetB, time);
    }

    function cancelPromise(uint id) external {
      require(msg.sender == promises[id].addrA, "This account is not involved in this promise");
      require(promises[id].addrB == address(0x0), "Promise cant be canceled once active");

      IERC20 tokenA = IERC20(promises[id].assetA);
      tokenA.transfer(promises[id].addrA, promises[id].amountA);

      bytes32 pairID = getPairID(promises[id].assetA, promises[id].assetB);
      uint index = getIndexOfPromise(id, promises[id].assetA, promises[id].assetB);

      delete joinablePromises[pairID][index];
    }

    function joinPromise(uint id) external {
      require(promises[id].addrB == address(0x0), "This promise has already been joined");

      IERC20 token = IERC20(promises[id].assetB);
      token.transferFrom(msg.sender, address(this), promises[id].amountB / 2);
      promises[id].owedB = promises[id].amountB / 2;

      promises[id].addrB = msg.sender;

      bytes32 pairID = getPairID(promises[id].assetA, promises[id].assetB);

      uint index = getIndexOfPromise(id, promises[id].assetA, promises[id].assetB);
      delete joinablePromises[pairID][index];

      accountPromises[msg.sender].push(id);

      emit PromiseJoined(msg.sender, id);
    }

    function payPromise(uint id, uint amount) external {
      require(promises[id].addrB != address(0x0), "This promise hasn't been joined yet");
      require(promises[id].time >= block.timestamp, "This promise is no longer active");
      require(msg.sender == promises[id].addrA || msg.sender == promises[id].addrB, "This account is not involved in this promise");

      IERC20 token;

      if (msg.sender == promises[id].addrA) {
        require(promises[id].owedA <= amount);
        token = IERC20(promises[id].assetA);
        token.transfer(promises[id].addrA, promises[id].amountA / 2);
        promises[id].owedA -= amount;
      }

      if (msg.sender == promises[id].addrB) {
        require(promises[id].owedB <= amount);
        token = IERC20(promises[id].assetB);
        token.transfer(promises[id].addrB, promises[id].amountB / 2);
        promises[id].owedB -= amount;
      }

    }

    function executePromise(uint id) external {
      require(promises[id].time <= block.timestamp, "This promise has not expired yet");
      require(promises[id].executed = false, "This promise has been executed");
      require(msg.sender == promises[id].addrA || msg.sender == promises[id].addrB, "This account is not involved in this promise");

      IERC20 tokenA = IERC20(promises[id].assetA);
      IERC20 tokenB = IERC20(promises[id].assetB);

      if (promises[id].owedA == 0 && promises[id].owedB == 0) {
        tokenA.transfer(promises[id].addrB, promises[id].amountA);
        tokenB.transfer(promises[id].addrA, promises[id].amountB);

      } else if (promises[id].owedA == 0 && promises[id].owedB > 0) {
        tokenA.transfer(promises[id].addrA, promises[id].amountA);
        tokenB.transfer(promises[id].addrA, promises[id].amountB);

      } else if (promises[id].owedB == 0 && promises[id].owedA > 0) {
        tokenA.transfer(promises[id].addrB, promises[id].amountA);
        tokenB.transfer(promises[id].addrB, promises[id].amountB);

      } else if (promises[id].owedA == 0 || promises[id].owedB == 0) {
        tokenA.transfer(promises[id].addrA, promises[id].amountA);
        tokenB.transfer(promises[id].addrB, promises[id].amountB);

      }

      promises[id].executed = true;

      emit PromiseExecuted(msg.sender, id);
    }


    function getPairID(address assetA, address assetB) public view returns (bytes32) {
         return sha256(abi.encodePacked(assetA, assetB));
    }

    function getIndexOfPromise(uint id, address assetA, address assetB) internal view returns (uint) {
      bytes32 pairId = getPairID(assetA, assetB);
      uint index;
        for (uint i = 0; i < joinablePromises[pairId].length; ++i) {
          if (joinablePromises[pairId][i] == id) {
            index = i;
            break;
            }
          } 
      return index;
    }

       /** Get Promise Arrays

       How to use: getPromises_Asset_Amount(), getPromises_Time_Executed_Addr(), getPromises_owed()
       if you want promises specific to an account eg. a list of Promises 1 user is involved in
       input the account and true for accountPairSwitch (Doesn't matter what you put for assetA and assetB)

       if you want promises specific to a pair eg. a list of Promises for ETH/DAI 
       input false for accountPairSwitch and input the two token addresses as assetA & assetB

       **/


    function getPromises_Asset_Amount(address account, bool accountPairSwitch, address _assetA, address _assetB) external view returns (uint[] memory, uint[] memory, uint[] memory, address[] memory, address[] memory) {
         uint[] memory ids;
         if (accountPairSwitch) {
            ids = getAccountPromisesIDs(account);
         } else {
            ids = getJoinablePromisesIDs(_assetA, _assetB);
         }
         uint length = ids.length;
         uint[] memory amountA = new uint[](length);
         uint[] memory amountB = new uint[](length);
         address[] memory assetA = new address[](length);
         address[] memory assetB = new address[](length);

        for (uint i = 0; i < length; ++i) { 
          PromData memory promData = promises[ids[i]];
            if(!promData.executed) {
               amountA[i] = promData.amountA;
               amountB[i] = promData.amountB;
               assetA[i] = promData.assetA;
               assetB[i] = promData.assetB;
            }
          }

      return (ids, amountA, amountB, assetA, assetB);
    }
    function getPromises_Time_Executed_Addr(address account, bool accountPairSwitch, address _assetA, address _assetB) external view returns (uint[] memory, uint[] memory, bool[] memory, address[] memory, address[] memory) {
        uint[] memory ids;
        if (accountPairSwitch) {
             ids = getAccountPromisesIDs(account);
        } else {
             ids = getJoinablePromisesIDs(_assetA, _assetB);
           }
         uint length = ids.length;
         uint[] memory time = new uint[](length);
         bool[] memory executed = new bool[](length);
         address[] memory addrA = new address[](length);
         address[] memory addrB = new address[](length);

        for (uint i = 0; i < length; ++i) { 
          PromData memory promData = promises[ids[i]];
            if(!promData.executed) {
              time[i] = promData.time;
              executed[i] = promData.executed;
              addrA[i] = promData.addrA;
              addrB[i] = promData.addrB;
            }
          }

      return (ids, time, executed, addrA, addrB);
    }
    function getPromises_owed(address account, bool accountPairSwitch, address _assetA, address _assetB) external view returns (uint[] memory, uint[] memory, uint[] memory) {
        uint[] memory ids;
        if (accountPairSwitch) {
             ids = getAccountPromisesIDs(account);
        } else {
             ids = getJoinablePromisesIDs(_assetA, _assetB);
           }
         uint length = ids.length;
         uint[] memory owedA = new uint[](length);
         uint[] memory owedB = new uint[](length);

        for (uint i = 0; i < length; ++i) { 
          PromData memory promData = promises[ids[i]];
            if(!promData.executed) {
              owedA[i] = promData.owedA;
              owedB[i] = promData.owedB;
            }
          }

      return (ids, owedA, owedB);
    }

       /** ID Arrays **/

    function getAccountPromisesIDs(address account) public view returns (uint[] memory) {
      return accountPromises[account];
    }

    function getJoinablePromisesIDs(address assetA, address assetB) public view returns (uint[] memory) {
      return joinablePromises[getPairID(assetA, assetB)];
    }

       /** Single Promise Data **/

    function getPromiseData_Amount_Asset_Time_Executed(uint id) external view returns (uint, address, uint, address, uint, bool) {
      PromData memory promData = promises[id];
        return (promData.amountA, promData.assetA, promData.amountB, promData.assetB,
                promData.time, promData.executed);
    }

    function getAssetData_Name_Decimals(address assetA, address assetB) external view returns (string memory, string memory, uint, uint) {
      IERC20 tokenA = IERC20(assetA);
      IERC20 tokenB = IERC20(assetB);
        return (tokenA.name(), tokenB.name(), tokenA.decimals(), tokenB.decimals());
    }


}
