import React from "react";

export default function CreatePromiseButton(props) {
  if (props.amountA <= props.balanceAssetA) {
    if (props.approved) {
      return <button onClick={() => props.createPromise()}>Create Promise</button>;
    } else {
      return <button onClick={() => props.approve()}>Approve</button>;
    }
  } else {
    return <button>...</button>;
  }
}
