import React from "react";
import CreatePromiseButton from "./CreatePromiseButton";

export default function PromiseInput(props) {
  async function convertDate(date) {
    var dateParts = date.split("/");
    if (dateParts.join("").length === 8) {
      var dateObject = new Date(+dateParts[2], dateParts[1] - 1, +dateParts[0]).getTime() / 1000;
      props.setTime(dateObject);
    }
  }
  return (
    <div>
      <p>Your Asset address:</p>
      <input
        placeholder="Contract address"
        onChange={(event) => {
          props.setAssetA(event.target.value);
        }}
      ></input>
      <p>Your Asset amount:</p>
      <p>balance: {props.balanceAssetA / Math.pow(10, props.assetADeclimals)}</p>
      <input
        placeholder="0.00"
        onChange={(event) => {
          props.setAmountA((event.target.value * 1e18).toLocaleString("fullwide", { useGrouping: false }));
        }}
      ></input>
      <p>Participants Asset address:</p>
      <input
        placeholder="Contract address"
        onChange={(event) => {
          props.setAssetB(event.target.value);
        }}
      ></input>
      <p>Participants Asset amount:</p>
      <input
        placeholder="0.00"
        onChange={(event) => {
          props.setAmountB(event.target.value * 1e18).toLocaleString("fullwide", { useGrouping: false });
        }}
      ></input>
      <br />
      <p>time:</p>
      <input
        placeholder="DD/MM/YYYY"
        onChange={(event) => {
          convertDate(event.target.value);
        }}
      ></input>
    </div>
  );
}
