import React, { Component, useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import PromController from "./contracts/PromController.json";
import token from "./contracts/Token1.json";
import getWeb3 from "./getWeb3";

import ConnectWallet from "./components/ConnectWallet.js";
import CreatePromise from "./pages/CreatePromise.js";
import FindPromise from "./pages/FindPromise.js";

import "./App.css";

export default function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);
  const [prom, setProm] = useState(null);
  const [assetA, setAssetA] = useState(null);
  const [assetB, setAssetB] = useState(null);
  const [amountA, setAmountA] = useState(null);
  const [amountB, setAmountB] = useState(null);
  const [time, setTime] = useState(null);
  const [assetInstance, setAssetInstance] = useState(null);
  const [assetADeclimals, setAssetADeclimals] = useState(null);
  const [approved, setApproved] = useState(null);
  const [balanceAssetA, setBalanceAssetA] = useState(null);

  const [joinablePromises, setJoinablePromises] = useState(null);
  const [activePromises, setActivePromises] = useState(null);
  const [pairID, setPairID] = useState(null);

  const setUpWeb3 = async () => {
    try {
      const _web3 = await getWeb3();

      const accounts = await _web3.eth.getAccounts();

      const networkId = await _web3.eth.net.getId();
      const deployedNetwork = PromController.networks[networkId];

      setProm(new _web3.eth.Contract(PromController.abi, deployedNetwork && deployedNetwork.address));
      setWeb3(_web3);
      setAccount(accounts[0]);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(`Failed to load web3, accounts, or contract. Check console for details.`);
      console.error(error);
    }
  };
  setUpWeb3();
  useEffect(() => {
    if (assetA) {
      if (assetInstance) {
        checkBalance();
        checkAllowance();
      } else if (web3.utils.isAddress(assetA)) {
        createTokenInstance();
      }
    }
  }, [assetA, amountA]);

  async function getAccount() {
    await window.ethereum
      .request({
        method: "eth_requestAccounts",
      })
      .then((result) => {
        setAccount(result[0]);
      });
  }
  async function createTokenInstance() {
    var _assetInstance = new web3.eth.Contract(token.abi, assetA);
    var decimals = await _assetInstance.methods.decimals().call();
    setAssetInstance(_assetInstance);
    setAssetADeclimals(decimals);
  }

  async function checkBalance() {
    var balanceOf = await assetInstance.methods.balanceOf(account).call();
    setBalanceAssetA(balanceOf);
  }

  async function checkAllowance() {
    var allo = await assetInstance.methods.allowance(account, prom._address).call();
    if (allo >= amountA) {
      setApproved(true);
    } else {
      setApproved(false);
    }
  }

  async function approve(conAddr) {
    await assetInstance.methods
      .approve(prom._address, (2 ** 256).toLocaleString("fullwide", { useGrouping: false }))
      .send({ from: account })
      .then((result) => {
        console.log(result);
        setApproved(true);
      });
  }

  async function createPromise() {
    // 2 test token addresses
    //0x714293A0fEbC7fBC1225B3A2f63C71D091dC1183
    //0xd72915144517e82e86926926d1308a44455eb6b0
    await prom.methods
      .createPromise(
        amountA.toLocaleString("fullwide", { useGrouping: false }),
        assetA,
        amountB.toLocaleString("fullwide", { useGrouping: false }),
        assetB,
        time.toString()
      )
      .send({ from: account })
      .then((result) => {
        console.log("created promise " + result.hash);
        setAssetA(null);
        setAssetB(null);
        setAmountA(null);
        setAmountB(null);
        setTime(null);
      });
  }

  async function getPairID() {
    setPairID(await prom.methods.getJoinablePromises(assetA, assetB).call());
  }
  async function getAllPromises() {
    var promiseIds = await prom.methods.getJoinablePromises().call();
    var _joinablePromises = [];
    var activePromises = [];
    for (var i = 0; i < promiseIds.length; i++) {
      var a = promiseIds[i].toString();
      var p = await prom.methods.getPromise(a).call();
      _joinablePromises.push(p);
    }

    setJoinablePromises(_joinablePromises);
    setActivePromises(await prom.methods.getActivePromises().call());
  }

  return (
    <Router>
      <div class="App">
        <h1>Promise ü§ù</h1>
        <ConnectWallet getAccount={() => getAccount()} account={account} />
        <Link to="/Create">Create Promise</Link>
        <br />
        <Link to="/Find" onClick={() => getAllPromises()}>
          Find Promise
        </Link>
        <Switch>
          <Route
            path="/Create"
            exact
            render={(props) => (
              <CreatePromise
                setAssetA={(a) => setAssetA(a)}
                setAssetB={(a) => setAssetB(a)}
                setAmountA={(a) => setAmountA(a)}
                setAmountB={(a) => setAmountB(a)}
                setTime={(t) => setTime(t)}
                createPromise={() => createPromise()}
                approve={() => approve()}
                approved={approved}
                balanceAssetA={balanceAssetA}
                assetADeclimals={assetADeclimals}
                amountA={amountA}
              />
            )}
          />
          <Route
            path="/Find"
            exact
            render={(props) => <FindPromise joinablePromises={joinablePromises} activePromises={activePromises} />}
          />
        </Switch>
      </div>
    </Router>
  );
}
