import React from "react";

import BalanceOfToken from "./BalanceOfToken.js";

export default function PromiseInput(props) {
  return (
    <div>
      <p>Your Asset address:</p>
      <input
        placeholder="Contract address"
        onChange={(event) => {
          props.setAssetA(event.target.value * 1e18);
        }}
      ></input>

      <p>Your Asset amount:</p>
      <input placeholder="0.00"></input>
      <BalanceOfToken />
      <p>Participants Asset address:</p>
      <input placeholder="Contract address"></input>
      <p>Participants Asset amount:</p>
      <input placeholder="0.00"></input>
      <br />
      <p>time:</p>
      <input placeholder="DD/MM/YYYY"></input>
    </div>
  );
}
