import React, { Component, useState } from "react";
import PromController from "./contracts/PromController.json";
import token from "./contracts/Token1.json";
import getWeb3 from "./getWeb3";

import "./App.css";

function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);
  const [prom, setProm] = useState(null);

  const componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = PromController.networks[networkId];
      setProm(new web3.eth.Contract(PromController.abi, deployedNetwork && deployedNetwork.address));
      setToken(new web3.eth.Contract(PromController.abi, deployedNetwork && deployedNetwork.address));
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(`Failed to load web3, accounts, or contract. Check console for details.`);
      console.error(error);
    }
  };

  return <div class="App"></div>;
}

export default App;
