import React, { Component, useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Link, useLocation } from "react-router-dom";
import PromController from "./contracts/PromController.json";
import token from "./contracts/Token1.json";
import getWeb3 from "./getWeb3";

import ConnectWallet from "./components/ConnectWallet.js";
import CreatePromise from "./pages/CreatePromise.js";
import FindPromise from "./pages/FindPromise.js";

import "./App.css";

export default function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);
  const [promInstance, setPromInstance] = useState(null);

  const [assetA, setAssetA] = useState(null);
  const [assetB, setAssetB] = useState(null);
  const [amountA, setAmountA] = useState(null);
  const [amountB, setAmountB] = useState(null);
  const [time, setTime] = useState(null);

  const [assetAInstance, setAssetAInstance] = useState(null);
  const [assetBInstance, setAssetBInstance] = useState(null);
  const [assetADeclimals, setAssetADeclimals] = useState(null);
  const [assetBDeclimals, setAssetBDeclimals] = useState(null);

  const [approved, setApproved] = useState(null);
  const [balanceAssetA, setBalanceAssetA] = useState(null);

  const setUpWeb3 = async () => {
    try {
      const _web3 = await getWeb3();

      const accounts = await _web3.eth.getAccounts();

      const networkId = await _web3.eth.net.getId();
      const deployedNetwork = PromController.networks[networkId];

      setPromInstance(new _web3.eth.Contract(PromController.abi, deployedNetwork && deployedNetwork.address));
      setWeb3(_web3);
      setAccount(accounts[0]);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(`Failed to load web3, accounts, or contract. Check console for details.`);
      console.error(error);
    }
  };
  setUpWeb3();

  async function getAccount() {
    await window.ethereum
      .request({
        method: "eth_requestAccounts",
      })
      .then((result) => {
        setAccount(result[0]);
      });
  }
  async function createTokenInstance(k) {
    let assetInstance, decimals;
    if ((k = "A")) {
      assetInstance = new web3.eth.Contract(token.abi, assetA);
      decimals = await assetInstance.methods.decimals().call();
      setAssetAInstance(assetInstance);
      setAssetADeclimals(decimals);
    } else {
      assetInstance = new web3.eth.Contract(token.abi, assetB);
      decimals = await assetInstance.methods.decimals().call();
      setAssetBInstance(assetInstance);
      setAssetBDeclimals(decimals);
    }
  }

  async function checkBalance(k) {
    if ((k = "A")) {
      var balanceOf = await assetAInstance.methods.balanceOf(account).call();
      setBalanceAssetA(balanceOf);
    } else {
      var balanceOf = await assetBInstance.methods.balanceOf(account).call();
      setBalanceAssetB(balanceOf);
    }
  }

  async function checkAllowance(k) {
    let allo;
    if ((k = "A")) {
      allo = await assetAInstance.methods.allowance(account, promInstance._address).call();
      if (allo >= amountA) {
        setApproved(true);
      } else {
        setApproved(false);
      }
    } else {
      allo = await assetBInstance.methods.allowance(account, promInstance._address).call();
      if (allo >= amountB) {
        setApproved(true);
      } else {
        setApproved(false);
      }
    }
  }

  async function approve(k) {
    if ((k = "A")) {
      await assetAInstance.methods
        .approve(promInstance._address, (2 ** 256).toLocaleString("fullwide", { useGrouping: false }))
        .send({ from: account })
        .then((result) => {
          console.log(result);
          setApproved(true);
        });
    } else {
      await assetBInstance.methods
        .approve(promInstance._address, (2 ** 256).toLocaleString("fullwide", { useGrouping: false }))
        .send({ from: account })
        .then((result) => {
          console.log(result);
          setApproved(true);
        });
    }
  }

  return (
    <Router>
      <div class="App">
        <h1>Promise 🤝</h1>
        <ConnectWallet getAccount={() => getAccount()} account={account} />
        <Link to="/Create">Create Promise</Link>
        <br />
        <Link to="/Find">Find Promise</Link>
        <Switch>
          <Route
            path="/Create"
            exact
            render={(props) => (
              <CreatePromise
                web3={web3}
                promInstance={promInstance}
                assetA={assetA}
                assetB={assetB}
                amountB={amountB}
                amountA={amountA}
                time={time}
                account={account}
                approved={approved}
                balanceAssetA={balanceAssetA}
                assetADeclimals={assetADeclimals}
                assetAInstance={assetAInstance}
                setAssetA={(a) => setAssetA(a)}
                setAssetB={(a) => setAssetB(a)}
                setAmountA={(a) => setAmountA(a)}
                setAmountB={(a) => setAmountB(a)}
                setTime={(t) => setTime(t)}
                approve={(k) => approve(k)}
                createTokenInstance={(k) => createTokenInstance(k)}
                checkAllowance={(k) => checkAllowance(k)}
                checkBalance={(k) => checkBalance(k)}
              />
            )}
          />
          <Route
            path="/Find"
            exact
            render={(props) => (
              <FindPromise
                web3={web3}
                promInstance={promInstance}
                assetA={assetA}
                assetB={assetB}
                setAmountA={(a) => setAmountA(a)}
                setAmountB={(a) => setAmountB(a)}
                assetAInstance={assetAInstance}
                assetBInstance={assetBInstance}
                createTokenInstance={(k) => createTokenInstance(k)}
                checkAllowance={(k) => checkAllowance(k)}
                checkBalance={(k) => checkBalance(k)}
              />
            )}
          />
        </Switch>
      </div>
    </Router>
  );
}
