import React, { Component, useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import PromController from "./contracts/PromController.json";
import token from "./contracts/Token1.json";
import getWeb3 from "./getWeb3";

import ConnectWallet from "./components/ConnectWallet.js";
import CreatePromise from "./pages/CreatePromise.js";
import FindPromise from "./pages/FindPromise.js";

import "./App.css";

export default function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);
  const [prom, setProm] = useState(null);
  const [tokenAddressA, setTokenAddressA] = useState(null);
  const [tokenAddressB, setTokenAddressB] = useState(null);

  const componentDidMount = async () => {
    try {
      const web3 = await getWeb3();

      const accounts = await web3.eth.getAccounts();

      const networkId = await web3.eth.net.getId();
      const deployedNetwork = PromController.networks[networkId];

      setProm(new web3.eth.Contract(PromController.abi, deployedNetwork && deployedNetwork.address));

      setAccount(accounts[0]);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(`Failed to load web3, accounts, or contract. Check console for details.`);
      console.error(error);
    }
  };
  async function approve(addressA, addressB, amountA, amountB) {
    tokenA = await new web3.eth.Contract(token.abi, addressA);
    tokenB = await new web3.eth.Contract(token.abi, addressB);

    var alloA = await dai.methods
      .allowance(account, daiFarm._address)
      .call()
      .then((result) => {
        console.log("approved");
      });

    await tokenA.methods
      .approve(prom.address, (2 ** 256).toLocaleString("fullwide", { useGrouping: false }))
      .send({ from: account })
      .then((result) => {
        console.log("approved");
      });
  }
  async function getAccount() {
    await window.ethereum
      .request({
        method: "eth_requestAccounts",
      })
      .then((result) => {
        setAccount(result[0]);
      });
  }

  return (
    <Router>
      <div class="App">
        <h1>Promise ğŸ¤</h1>
        <ConnectWallet getAccount={() => getAccount()} account={account} />
        <Link to="/Create">Create Promise</Link>
        <br />
        <Link to="/Find">Find Promise</Link>
        <Switch>
          <Route path="/Create" exact render={(props) => <CreatePromise />} />
          <Route path="/Find" exact render={(props) => <FindPromise />} />
        </Switch>
      </div>
    </Router>
  );
}
