import React, { useState, useEffect } from "react";

import TableOfPromises from "../components/TableOfPromises.js";
import TokenAddressInput from "../components/TokenAddressInput.js";

export default function FindPromise({
  web3,
  promInstance,
  assetA,
  assetB,
  amountA,
  amountB,
  setAssetA,
  setAssetB,
  setAmountA,
  setAmountB,
  assetAInstance,
  assetBInstance,
  createTokenInstance,
  checkAllowance,
  checkBalance,
}) {
  const [pairID, setPairID] = useState(null);
  const [joinablePromises, setJoinablePromises] = useState(null);

  useEffect(() => {
    console.log("here");
    if (assetB) {
      console.log("here..");
      if (assetBInstance) {
        console.log("here...");
        checkBalance("B");
        checkAllowance("B");
      } else if (web3.utils.isAddress(assetB)) {
        createTokenInstance("B");
      }
    }
    if (assetA) {
      if (assetAInstance) {
        checkBalance("A");
        checkAllowance("A");
      } else if (web3.utils.isAddress(assetA)) {
        createTokenInstance("A");
      }
    }
    if (assetAInstance && assetBInstance) {
      getAllPromises();
    }
  }, [assetA, assetB]);

  async function getPairID() {
    setPairID(await promInstance.methods.getJoinablePromises(assetA, assetB).call());
  }

  async function getAllPromises() {
    var promiseIds = await promInstance.methods.getJoinablePromises(assetA, assetB).call();
    var joinablePromises = [];

    for (var i = 0; i < promiseIds.length; i++) {
      var a = promiseIds[i].toString();
      var p = await promInstance.methods.getPromise(a).call();

      joinablePromises.push(p);
    }
    console.log("bb");

    setJoinablePromises(joinablePromises);
  }
  return (
    <div>
      <TokenAddressInput setAssetA={(a) => setAssetA()} setAssetB={(a) => setAssetB()} />
      <TableOfPromises joinablePromises={joinablePromises} />
    </div>
  );
}
