import React from "react";

import PromiseInput from "../components/PromiseInput.js";
import CreatePromiseButton from "../components/CreatePromiseButton.js";

export default function CreatePromise(props) {
  function handleCreatePromise() {}

  return (
    <div>
      <p>Create a Promise</p>
      <PromiseInput
        setAssetA={() => props.setAssetA()}
        setAssetB={() => props.setAssetB()}
        setAmountA={() => props.setAmountA()}
        setAmountB={() => props.setAmountB()}
        setTime={() => props.setTime()}
        checkAllowance={() => props.checkAllowance()}
        balanceAssetA={props.balanceAssetA}
      />
      <CreatePromiseButton
        createPromise={() => props.createPromise()}
        approve={() => props.approve()}
        approved={props.approved}
        balanceOfAssetA={props.balanceAssetA}
        amountA={props.amountA}
      />
    </div>
  );
}
