import React from "react";

export default function PromiseInput(props) {
  return (
    <div>
      <p>Your Asset address:</p>
      <input
        placeholder="Contract address"
        onChange={
          ((event) => {
            props.setAssetA(event.target.value);
          },
          () => {
            props.checkAllowance();
          })
        }
      ></input>
      <p>Your Asset amount:</p>
      <p>balance: {props.balanceAssetA}</p>
      <input
        placeholder="0.00"
        onChange={(event) => {
          props.setAmountA(event.target.value * 1e18);
          props.checkAllowance();
        }}
      ></input>
      <p>Participants Asset address:</p>
      <input
        placeholder="Contract address"
        onChange={(event) => {
          props.setAssetB(event.target.value * 1e18);
        }}
      ></input>
      <p>Participants Asset amount:</p>
      <input
        placeholder="0.00"
        onChange={(event) => {
          props.setAmountB(event.target.value * 1e18);
        }}
      ></input>
      <br />
      <p>time:</p>
      <input
        placeholder="DD/MM/YYYY"
        onChange={(event) => {
          props.setTime(event.target.value);
        }}
      ></input>
    </div>
  );
}
