import React from "react";

import PromiseInput from "../components/PromiseInput.js";
import CreatePromiseButton from "../components/CreatePromiseButton.js";

export default function CreatePromise(props) {
  async function createPromise() {
    // 2 test token addresses
    //0x714293A0fEbC7fBC1225B3A2f63C71D091dC1183
    //0xd72915144517e82e86926926d1308a44455eb6b0
    await prom.methods
      .createPromise(
        amountA.toLocaleString("fullwide", { useGrouping: false }),
        assetA,
        amountB.toLocaleString("fullwide", { useGrouping: false }),
        assetB,
        time.toString()
      )
      .send({ from: account })
      .then((result) => {
        console.log("created promise " + result.hash);
        setAssetA(null);
        setAssetB(null);
        setAmountA(null);
        setAmountB(null);
        setTime(null);
      });
  }

  return (
    <div>
      <p>Create a Promise</p>
      <PromiseInput
        setAssetA={(a) => props.setAssetA(a)}
        setAssetB={(a) => props.setAssetB(a)}
        setAmountA={(a) => props.setAmountA(a)}
        setAmountB={(a) => props.setAmountB(a)}
        setTime={(t) => props.setTime(t)}
        balanceAssetA={props.balanceAssetA}
        assetADeclimals={props.assetADeclimals}
      />
      <CreatePromiseButton
        createPromise={() => createPromise()}
        approve={() => props.approve()}
        approved={props.approved}
        balanceAssetA={props.balanceAssetA}
        amountA={props.amountA}
      />
    </div>
  );
}
